$db->query_write("
	CREATE TABLE IF NOT EXISTS `" . TABLE_PREFIX . "ssgtiblogfeedposter` (
		`blogfeedid` int(10) unsigned NOT NULL AUTO_INCREMENT,
		`title` varchar(250) NOT NULL DEFAULT '',
		`description` varchar(250) NOT NULL DEFAULT '',
		`displayorder` int(10) unsigned NOT NULL DEFAULT '0',
		`url` text,
		`valid` smallint(5) unsigned NOT NULL DEFAULT '0',
		`dateline` int(10) unsigned NOT NULL DEFAULT '0',
		`port` smallint(5) unsigned NOT NULL DEFAULT '80',
		`ttl` smallint(5) unsigned NOT NULL DEFAULT '1500',
		`maxresults` smallint(6) NOT NULL DEFAULT '0',
		`userid` int(10) unsigned NOT NULL DEFAULT '0',
		`titletemplate` mediumtext,
		`bodytemplate` mediumtext,
		`searchwords` mediumtext,
		`blogtype` enum('visible','draft') NOT NULL DEFAULT 'visible',
		`feedoptions` int(10) unsigned NOT NULL DEFAULT '0',
		`emailupdate` enum('none','usercp','email') NOT NULL DEFAULT 'none',
		`options` int(10) unsigned NOT NULL DEFAULT '0',
		`lastrun` int(10) unsigned NOT NULL DEFAULT '0',
		PRIMARY KEY (`blogfeedid`),
		KEY `lastrun` (`lastrun`)
	) $enginetype=MyISAM
");

$db->query_write("
	CREATE TABLE IF NOT EXISTS `" . TABLE_PREFIX . "ssgtiblogfeedposter_log` (
		`blogfeedid` int(10) unsigned NOT NULL DEFAULT '0',
		`blogid` int(10) unsigned NOT NULL DEFAULT '0',
		`blogtype` enum('visible','draft') NOT NULL DEFAULT 'visible',
		`uniquehash` char(32) NOT NULL DEFAULT '',
		`contenthash` char(32) NOT NULL DEFAULT '',
		`dateline` int(10) unsigned NOT NULL DEFAULT '0',
		PRIMARY KEY (`blogfeedid`,`blogid`,`blogtype`),
		UNIQUE KEY `uniquehash` (`uniquehash`)
	) $enginetype=MyISAM
");

