code.uninstallcode:
$db->hide_errors();
require_once(DIR . '/includes/class_dbalter.php');
$db_alter =& new vB_Database_Alter_MySQL($db);

$db->query_write("DROP TABLE IF EXISTS " . TABLE_PREFIX . "ssgtiblogfeedposter");
$db->query_write("DROP TABLE IF EXISTS " . TABLE_PREFIX . "ssgtiblogfeedposter_log");

if ($db_alter->fetch_table_info('usergroup'))
{
	$db_alter->drop_field('ssgti_blogfeedposter_perms');
	$db_alter->drop_field('ssgtiblogfeedpostermax');
}

if ($db_alter->fetch_table_info('blog'))
{
	$db_alter->drop_field('ssgtiblogfeedposter');
	$db_alter->drop_field('ssgtiblogfeedposter_dateline');
}

build_forum_permissions();
$db->show_errors();

